import React, { useState } from 'react';
import Counter from './components/Counter';
import ClassCounter from './components/ClassCounter';
import PostItem from './components/PostItem';
import PostList from './components/PostList';

import './styles/app.css';

function App() {

  // const [likes, setLikes] = useState(0);
  // console.log(likes);
  // console.log(setLikes);

  const [value, setValue] = useState('text in Input');
  const [posts, setPosts] = useState([
    { id: 1, titel: 'Javascript 1', body: 'описание поста 1' },
    { id: 2, titel: 'Javascript 2', body: 'описание поста 2' },
    { id: 3, titel: 'Javascript 3', body: 'описание поста 3' },
    { id: 4, titel: 'Javascript 4', body: 'описание поста 4' }
  ]);
  const [posts2, setPosts2] = useState([
    { id: 1, titel: 'Javascript 5', body: 'описание поста 5' },
    { id: 2, titel: 'Javascript 6', body: 'описание поста 6' },
    { id: 3, titel: 'Javascript 7', body: 'описание поста 7' },
    { id: 4, titel: 'Javascript 8', body: 'описание поста 8' }
  ]);
  return (
    <div className="App">

      <h1>{value}</h1>
      <input type='text'
        value={value}
        onChange={event => setValue(event.target.value)}
      ></input>

      {/* <Counter />
      <ClassCounter /> */}


      {/* <PostItem post={{ id: 1, titel: 'Javascript', body: 'описание поста' }} /> */}
      {/* <h1 style={{ textAlign: 'center' }}>
        Список постов
      </h1>

      {posts.map((post) => <PostItem post={post} key={post.id} />)} */}

      <PostList posts={posts} titel='Список постов titel 1' />

      <PostList posts={posts2} titel='Список постов titel 2' />

    </div>
  );
}

export default App;
