import React from "react";
import MyInput from './UI/input/MyInput'
import MyButton from "./UI/button/MyButton";
import { useState } from "react";

const PostForm = ({ create }) => {

    const [post, setPost] = useState({ titel: '', body: '' });
    const addNewPost = (e) => {
        e.preventDefault();
        const newPost = { ...post, id: Date.now() };
        create(newPost);
        setPost({ titel: '', body: '' })
    };
    return (<form>
        <MyInput value={post.titel} onChange={e => setPost({ ...post, titel: e.target.value })} type='text' placeholder='название поста' />
        <MyInput value={post.body} onChange={e => setPost({ ...post, body: e.target.value })} type='text' placeholder='описание поста' />
        <MyButton onClick={addNewPost} >Создать пост</MyButton>
    </form>);

}
export default PostForm;
