import React from "react";
import PostItem from "./PostItem";
import { TransitionGroup } from "react-transition-group";

const PostList = ({ posts, titel, remove }) => {
    if (!posts.length) {
        return <div><h1 style={{ textAlign: 'center' }}>Посты небыли найдены!</h1></div>
    }

    return (
        <div>
            <h1 style={{ textAlign: 'center' }}>
                {titel}
            </h1>
            <TransitionGroup>
                {posts.map((post, index) =>
                    <CSSTransition
                        key={post.id}
                        timeout={500}
                        classNames="item"
                    >
                        <PostItem remove={remove}
                            number={index + 1}
                            post={post} />
                    </CSSTransition>
                )}
            </TransitionGroup>
        </div>
    )
}

export default PostList;
