import React, { useMemo, useState } from 'react';
import PostForm from './components/PostForm';
import PostList from './components/PostList';
import './styles/app.css';
import PostFilter from './components/PostFilter';
import MyModal from './components/UI/modal/MyModal';
import MyButton from './components/UI/button/MyButton';
import { usePosts } from './components/hooks/usePosts';

function App() {

  const [posts, setPosts] = useState([
    { id: 1, titel: 'Javascript 2', body: 'описание поста 4' },
    { id: 2, titel: 'Javascript 1', body: 'описание поста 2' },
    { id: 3, titel: 'Javascript 4', body: 'описание поста 1' },
    { id: 4, titel: 'Javascript 3', body: 'описание поста 3' }
  ]);

  const createPost = (newPost) => {
    setPosts([...posts, newPost]);
    setModal(false)
  }
  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id));
  }

  const [filter, setFilter] = useState({ sort: '', query: '' });
  const [modal, setModal] = useState(false);

  const sortedAndSearchingPosts = usePosts(posts, filter.sort, filter.query);

  return (
    <div className="App">
      <MyButton style={{ marginTop: '25px' }} onClick={() => { setModal(true) }}>Создать пост</MyButton>
      <MyModal visible={modal} setVisible={setModal}><PostForm create={createPost} /></MyModal>
      <hr style={{ margin: '15px 0' }} />
      <PostFilter filter={filter} setFilter={setFilter} />
      <PostList remove={removePost} posts={sortedAndSearchingPosts} titel='Список постов titel 1' />
    </div>
  );
}

export default App;
