import React, { useState } from 'react';

function App() {

  // const [likes, setLikes] = useState(0);
  // console.log(likes);
  // console.log(setLikes);

  const [value, setValue] = useState('text in Input');


  return (
    <div className="App">
      <h1>{value}</h1>
      <input type='text'
        value={value}
        onChange={event => setValue(event.target.value)}
      ></input>

    </div>
  );
}

export default App;
