import React from 'react';

function App() {
  let likes = 0;

  function Increment() {
    likes += 1;
    console.log(likes);
  }
  // function Decriment() {
  //   likes += 1;
  //   console.log(likes);
  // }


  return (
    <div className="App">
      <h1>{likes}</h1>
      <button onClick={Increment}>Increment</button>
      <button onClick={() => likes -= 1}>Decriment</button>
    </div>
  );
}

export default App;
