import PostList from './components/PostList';
import MySelect from './components/UI/select/MySelect';
// import MyButton from './components/UI/button/MyButton';
import MyInput from './components/UI/input/MyInput'

import './styles/app.css';

function App() {

  const [posts, setPosts] = useState([
    { id: 1, titel: 'Javascript 2', body: 'описание поста 4' },
    { id: 2, titel: 'Javascript 1', body: 'описание поста 2' },
    { id: 3, titel: 'Javascript 4', body: 'описание поста 1' },
    { id: 4, titel: 'Javascript 3', body: 'описание поста 3' }
  ]);

  // const [titel, setTitel] = useState('');
  // const [body, setBody] = useState('');

  // const [post, setPost] = useState({ titel: '', body: '' });

  // const bodyInputRef = useRef();

  const createPost = (newPost) => {
    setPosts([...posts, newPost]);
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id));
  }

  const [selectedSort, setSelectedSort] = useState('');

  const sortedPosts = useMemo(() => {
    console.log('Sorted post runnin')
    if (selectedSort)
      return [...posts].sort((a, b) => a[selectedSort].localeCompare(b[selectedSort]));
    return posts;
  }, [selectedSort, posts])

  const sortPosts = (sort) => {
    setSelectedSort(sort)
  }

  //Поиск

  const [searchQuary, setSearchQuary] = useState('');
  const sortedAndSearchingPosts = useMemo(() => {
    return sortedPosts.filter(post => post.titel.toLowerCase().includes(searchQuary.toLocaleLowerCase()));
  }, [searchQuary, sortedPosts])

  return (
    <div className="App">

      {/* <h1>{value}</h1>
      <input type='text'
        value={value}
        onChange={event => setValue(event.target.value)}
      ></input> */}

      {/* <Counter />
      <ClassCounter /> */}


      {/* <PostItem post={{ id: 1, titel: 'Javascript', body: 'описание поста' }} /> */}
      {/* <h1 style={{ textAlign: 'center' }}>
        Список постов
      </h1>

      {posts.map((post) => <PostItem post={post} key={post.id} />)} */}

      {/* <PostList posts={posts} titel='Список постов titel 1' /> */}

      {/* <PostList posts={posts2} titel='Список постов titel 2' /> */}


      {/* <form> */}
      {/* управляемый компонент */}
      {/* <MyInput value={post.titel} onChange={e => setTitel(e.target.value)} type='text' placeholder='название поста' /> */}
      {/* <input ref={bodyInputRef} type='text' /> */}

      {/* неуправляемый компонент */}
      {/* <MyInput ref={bodyInputRef} type='text' placeholder='описание поста' /> */}

      {/* <MyInput value={post.body} onChange={e => setBody(e.target.value)} type='text' placeholder='описание поста' /> */}

      {/* <MyInput value={post.titel} onChange={e => setPost({ ...post, titel: e.target.value })} type='text' placeholder='название поста' /> */}
      {/* <MyInput value={post.body} onChange={e => setPost({ ...post, body: e.target.value })} type='text' placeholder='описание поста' /> */}

      {/* <MyButton onClick={addNewPost} >Создать пост</MyButton> */}
      {/* </form> */}
      <PostForm create={createPost} />
      <hr style={{ margin: '15px 0' }} />
      {sortedAndSearchingPosts.length !== 0 ?
        <PostList remove={removePost} posts={sortedAndSearchingPosts} titel='Список постов titel 1' /> :
        <div><h1 style={{ textAlign: 'center' }}>Посты небыли найдены!</h1></div>
      }
    </div>
  );
}

export default App;
